import React from "react";
import DropDown from "./DropDown";
import Button from "./Button";

const AssignTab = () => {
  const tabStyle = {
    height: "100%",
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-evenly",
    padding: "10vh",
  };

  return (
    <div style={tabStyle}>
      <div>
        <span>ASSIGN TO: </span>
      </div>
      <DropDown
        label="assignTo"
        options={["Aaron", "Harmeet", "Megh", "Poojitha", "Nikola", "Tia"]}
        defaultVal="Name"
      />
      <DropDown
        title="exercises"
        options={["hamstring", "quadriceps", "calf", "ankle"]}
        defaultVal="Exercise"
      />
      <DropDown
        title="difficulty"
        options={["easy", "medium", "hard"]}
        defaultVal="Difficulty"
      />
      <Button name="ADD" class="ui button" style={{ alignSelf: "flex-end" }} />
    </div>
  );
};

export default AssignTab;
